        public void BuscarPorNombre(string Nombre)
        {
            comando = new SqlCommand("SP_BUSCAR_VACUNADOS_NOMBRE", con);
            comando.CommandType = CommandType.StoredProcedure;
            con.Open();
            comando.Parameters.AddWithValue("@NOMBRES", Nombre);
            leer = comando.ExecuteReader();
            while(leer.Read())
            {
                Persona persona = new Persona();
                persona.Nombres = leer.GetString("NOMBRES");
                persona.Apellidos = leer.GetString("APELLIDOS");
                persona.Telefono = leer.GetString("TELEFONO");
                persona.Nacimiento = leer.GetDateTime("FECHA_DE_NACIMIENTO");
                persona.Cedula = leer.GetString("CEDULA");
                persona.Provincia = leer.GetString("PROVINCIA");
                persona.VacunaRecibida = leer.GetString("VACUNA_RECIBIDA");
                persona.PrimeraDosis = leer.GetDateTime("FECHA_PRIMERA_DOSIS");
                


                dato.Add(persona);

            }
            con.Close();

        }
        public void BuscarPorApellido(string Apellido)
        {
